// 动态更新时间
function updateDateTime() {
    const now = new Date();
    const datetime = now.toLocaleString('zh-CN', { hour12: false });
    document.getElementById('datetime').textContent = datetime;
}

// 每秒更新一次时间
setInterval(updateDateTime, 1000);
updateDateTime();

